package day14;

public class EnhancedFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {1,2,3};
		for(int x: a) {
			System.out.println("values are "+x);
		}
		
		String names[]= {"ab","bc","cd"};
		for(String x : names) {
			System.out.println("String "+x);
		}
		

	}

}
