package day9;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=200;
		double d=a;
		System.out.println("Implicit type casting"+" "+ d);
		
		double d1=100.89;
		int b=(int)d1;
		
		System.out.println("Explicit type casting"+" "+b);
	}

}

